import { BandProtocolClient } from 'band.js';
import * as React from 'react';
import * as web3_1 from "web3";
import { Box, Title } from '../src/components/Box';
import { SendButton } from '../src/components/Buttons';
import { Wrapper } from '../src/components/Container';
import { Input } from '../src/components/Input';

const web3 = new web3_1.default('http://127.0.0.1:7545');

const bandClient: Promise<BandProtocolClient> = BandProtocolClient.make({
    provider: web3.currentProvider,
});

export default async (): Promise<any> => {
    const [client, setClient] = React.useState({ web3: {} });

    await bandClient.then((client: any) => {
        setClient(client);
    });

    console.log(client);


    return (
        <Wrapper>
            <Box>
                <Title>
                    Enter your name
                </Title>
                <div>
                    <Input placeholder='Enter your name' />
                </div>
                <SendButton>
                    Send
                </SendButton>
            </Box>
        </Wrapper>
    );
}
