import { BandProtocolClient } from 'band.js';
import * as React from 'react';
import { Fragment, useEffect, useState } from 'react';
import * as Web3 from 'web3';
import { OnBoardOne, OnBoardTwo } from './Home';

const web3 = new Web3.default('http://127.0.0.1:7545');

web3.setProvider('http://127.0.0.1:7545')
console.log(web3.currentProvider.host);

const useBandClient = (clients: any) => {
    const [client, setClient] = useState<BandProtocolClient>(clients);

    useEffect(() => {
        loadBandProtocol()
            .then((res: any) => {
                setClient(res);
            })
    }, []);

    const loadBandProtocol = async () => {
        const bandClient = await BandProtocolClient.make({ provider: web3.currentProvider.host, })
        return bandClient;
    }

    return {
        client,
    }
}

const useCreateCommunity = () => {
    const { client } = useBandClient({});

    const [community, setCommunity] = useState();

    useEffect(() => {
        loadBandProtocol()
            .then((res: any) => {
                setCommunity(res);
            })
    }, []);

    const loadBandProtocol = async () => {
        const communityClient = await client.createCommunity({
            name: "Example Community", // ERC-20 community token name
            symbol: "XEC", // ERC-20 community token symbol
            bonding: {
                collateralEquation: ["8", "1", "0", "2"], // x^2
                liquiditySpread: "30000000000000000" // 3%
            },
            params: {
                expirationTime: "300", // 5 minutes
                minParticipationPct: "10000000000000000", // 1%
                supportRequiredPct: "800000000000000000" // 80%
            }
        })
        return communityClient;
    }

    return {
        community
    }
}

export default () => {
    const [boarding, setOnboarding] = useState(1);
    const { client } = useBandClient({});
    return (
        <Fragment>
            {
                boarding === 1 && (
                    <OnBoardOne
                        onClick={() => {
                            setOnboarding(2)
                        }}
                    />
                )
            }

            {
                boarding === 2 && (
                    <OnBoardTwo />
                )
            }
        </Fragment>
    )
}
