import { BandProtocolClient } from 'band.js';
import * as React from 'react';
import { useState } from 'react';
import { useAsyncEffect } from 'use-async-effect';
// import * as web3_1 from "web3";
import { Box, Title } from '../src/components/Box';
import { SendButton } from '../src/components/Buttons';
import { Wrapper } from '../src/components/Container';
import { Input } from '../src/components/Input';

export default (): React.ReactNode => {
    const [client, setClient] = useState<BandProtocolClient>();


    useAsyncEffect(async () => {
        const bandClient = await BandProtocolClient.make({
            provider: window.web3.currentProvider,
        });

        setClient(bandClient);
    }, [])

    // const community = client.createCommunity({

    // })

    return (
        <Wrapper>
            <Box>
                <Title>
                    Enter your name
                </Title>
                <div>
                    <Input placeholder='Enter your name' />
                </div>
                <SendButton>
                    Send
                </SendButton>
            </Box>
        </Wrapper>
    );
}
