import { BandProtocolClient } from 'band.js';
import * as React from 'react';
import { Fragment, useState } from 'react';
import { useAsyncEffect } from 'use-async-effect';
import { OnBoardOne, OnBoardTwo } from './Home';

const useBandClient = (clients: any) => {
    const [client, setClient] = useState<BandProtocolClient>(clients);

    useAsyncEffect(async () => {
        loadBandProtocol()
    }, () => {
        []
    });

    const loadBandProtocol = async () => {
        const bandClient = await BandProtocolClient.make({ provider: window.web3.currentProvider, });
        setClient(bandClient);
    }

    if (!client) return {};

    return {
        client,
    }
}

export default () => {
    const [boarding, setOnboarding] = useState(1);
    const { client } = useBandClient({})

    console.log(client)

    return (
        <Fragment>
            {
                boarding === 1 && (
                    <OnBoardOne />
                )
            }

            {
                boarding === 2 && (
                    <OnBoardTwo />
                )
            }
        </Fragment>
    )
}
