import { BandProtocolClient } from 'band.js';
import CommunityClient from 'band.js/dist/services/CommunityClient';
import * as React from 'react';
import { Fragment, useEffect, useState } from 'react';
import Box from '../components/Box';
import Form from '../components/Form';

const useCreateCommunity = (clients: any, communityTitle: string, communitySymbol: string) => {
    const [community, setCommunity] = useState<CommunityClient>(clients);
    const [title, setTitle] = useState('');
    const [symbol, setSymbol] = useState('');

    const bandProtocolCreateCommunity = async (): Promise<any> => {
        // Set Protocol Provider
        const bandClient = await BandProtocolClient.make({ provider: window.web3.currentProvider, });

        // Create community client
        const communityClient = await bandClient.createCommunity({
            name: title,
            symbol: symbol,
            bonding: {
                collateralEquation: ["8", "1", "0", "2"],
                liquiditySpread: "30000000000000000"
            },
            params: {
                expirationTime: "300",
                minParticipationPct: "10000000000000000",
                supportRequiredPct: "800000000000000000"
            }
        });

        setTitle(communityTitle);
        setSymbol(communitySymbol);
        setCommunity(communityClient);
    }

    useEffect(() => {
        bandProtocolCreateCommunity()
    }, []);

    return {
        community
    }
}


export default (props) => {
    const [boarding, setOnboarding] = useState(3);
    const [communityName, setcommunityName] = useState('');
    const [communitySymbol, setcommunitySymbol] = useState('');

    // const { community } = useCreateCommunity({}, "My title", "MP");

    // console.log(community);
    console.log(props)
    return (
        <Fragment>
            {
                boarding === 1 && (
                    <Fragment>
                        <Box title="Create a community name">
                            <Form
                                title='Enter community name'
                                onSubmit={(text: any) => {
                                    setcommunityName(text)
                                    setOnboarding(2)
                                }}
                                onClick={() => console.log('hello')}
                            />
                        </Box>
                    </Fragment>
                )
            }

            {
                boarding === 2 && (
                    <Fragment>
                        <Box title="Create a community Symbol">
                            <Form
                                title='Enter community Symbol'
                                onSubmit={(text: any) => {
                                    setcommunitySymbol(text)

                                    setOnboarding(3)
                                }}
                                onClick={() => console.log('hello')}
                            />
                        </Box>
                    </Fragment>
                )
            }

            {
                boarding === 3 && (
                    <Fragment>
                        <h3 style={{ color: 'black' }} >
                            {'communityName'}
                        </h3>
                        <br />
                        <h3 style={{ color: 'black' }} >
                            {'communitySymbol'}
                        </h3>
                    </Fragment>
                )
            }
        </Fragment>
    )
}
