import { BandProtocolClient, IPFS } from 'band.js';
import * as React from 'react';
import Web3 from 'web3';
import { Wrapper } from '../src/components/Container';
import Home from '../src/models';
if (typeof window !== 'undefined') { require('the-lib'); }

const a = async () => {

    let bandClient = await BandProtocolClient.make({ provider: window.web3.currentProvider, })

    const a = await bandClient.createCommunity({
        name: "Example Community", // ERC-20 community token name
        symbol: "XEC", // ERC-20 community token symbol
        bonding: {
            collateralEquation: ["8", "1", "0", "2"], // x^2
            liquiditySpread: "30000000000000000" // 3%
        },
        params: {
            expirationTime: "300", // 5 minutes
            minParticipationPct: "10000000000000000", // 1%
            supportRequiredPct: "800000000000000000" // 80%
        }
    })
    console.log(a);
}


export default (props: any): React.ReactNode => {

    return (
        <Wrapper>
            <Home />
        </Wrapper>
    );
}

window.BandProtocolClient = BandProtocolClient
window.IPFS = IPFS

window.bandInit = async () => {
    window.BandProtocolClient.setAPI('https://api-wip.rinkeby.bandprotocol.com')
    window.web4 = new Web3(window.web3.currentProvider)
    window.bandClient = await BandProtocolClient.make({
        provider: window.web4.currentProvider,
    })
}
