import { BandProtocolClient } from 'band.js';
import * as React from 'react';
import { Fragment, useEffect, useState } from 'react';
import { OnBoardOne, OnBoardTwo } from './Home';

const useBandClient = (clients: any) => {
    const [client, setClient] = useState<BandProtocolClient>(clients);
    const [community, setCommunity] = useState({});

    const loadBandProtocol = async () => {
        const bandClient = await BandProtocolClient.make({ provider: window.web3.currentProvider, })
        setClient(bandClient)
    }

    const loadBandProtocolCreateCommunity = async () => {
        const communityClient = await client.createCommunity({
            name: "Example Community", // ERC-20 community token name
            symbol: "XEC", // ERC-20 community token symbol
            bonding: {
                collateralEquation: ["8", "1", "0", "2"], // x^2
                liquiditySpread: "30000000000000000" // 3%
            },
            params: {
                expirationTime: "300", // 5 minutes
                minParticipationPct: "10000000000000000", // 1%
                supportRequiredPct: "800000000000000000" // 80%
            }
        })
        setCommunity(communityClient);
    }

    useEffect(() => {
        loadBandProtocol()
        loadBandProtocolCreateCommunity()
        .then((res) => console.log('INSIDE RES', res))
    }, []);

    return {
        community,
    }
}


export default () => {
    const [boarding, setOnboarding] = useState(1);
    const { community } = useBandClient({});

    console.log(community);

    return (
        <Fragment>
            {
                boarding === 1 && (
                    <OnBoardOne
                        onClick={() => {
                            setOnboarding(2)
                        }}
                    />
                )
            }

            {
                boarding === 2 && (
                    <OnBoardTwo />
                )
            }
        </Fragment>
    )
}
