// web3.setProvider('http://127.0.0.1:7545')

const useBandClient = (clients: any) => {
    const [client, setClient] = useState<BandProtocolClient>(clients);

    useEffect(() => {
        loadBandProtocol()
            .then((res: any) => {
                setClient(res);
            })
    }, []);

    const loadBandProtocol = async () => {
        const bandClient = await BandProtocolClient.make({ provider: window.web3.currentProvider, })
        return bandClient;
    }

    return {
        client,
    }
}

