import Link from 'next/link';
import * as React from 'react';
import { theme } from '../src/assets/styles/mainStyles';
import Box from '../src/components/Box';
import { Wrapper } from '../src/components/Container';
import { useCreateCommunity } from '../src/assets/helper/index';

interface IProps {
    query?: {
        title: string;
        symbol: string;
    }
}

export default class extends React.PureComponent<IProps> {
    static getInitialProps({ query }: any) {
        return { query }
    }

    render() {
        const { title, symbol } = this.props.query;
        return (
            <Wrapper>
                {
                    title.length <= 0 || symbol.length <= 0
                        ? (
                            <Link href='/'>
                                <h3 style={{ color: theme.colors.primary, cursor: 'pointer' }} >
                                    Go back Home to create a community
                                </h3>
                            </Link>
                        ) : (
                            <Community
                                title={title}
                                symbol={symbol}
                            />
                        )
                }
            </Wrapper>
        )
    }
}


interface IProps {
    title: string;
    symbol: string;
}

const Community = ({ title, symbol }: IProps) => {
    const { community } = useCreateCommunity({}, title, symbol);

    alert(
        `Created Community!!`
    )
    console.log('COMM',community);

    return (
        <React.Fragment>
            <Box title={`Community Name: ${title}`} >
                <h3 style={{ color: 'black' }} >
                    Community Symbol: {symbol}
                </h3>
            </Box>
        </React.Fragment>
    )
}
